 -- Find the total spending on players for each team.
SELECT Team, SUM(Price_in_cr) As TotalSpending 
FROM Players
Group By Team 
Order By TotalSpending DESC;

-- Find the top 3 highest-paid 'All Rounders' across all teams.
SELECT Player, Team, Price_in_cr
FROM Players
WHERE Role = 'All Rounders'
ORDER BY Price_in_cr DESC
LIMIT 3;
SELECT * FROM  IPLPlayers;
-- Find the highest-priced player in each team.
with CET_HP AS( 
Select TEAM, Max(Price_in_cr) AS MaxPrice
From IPLPlayers
Group By Team
)
Select i.Player, i.Team,hp.MaxPrice
From IPLPlayers i
Join CET_HP hp ON i.Team = hp.Team
Where i.Price_in_cr = hp.MaxPrice;
-- Rank players by their price with in each team and list the top 2 for every team.
WITH RankedPlayer AS (
    SELECT 
        Player, 
        Team, 
        Price_in_cr,
        ROW_NUMBER() OVER (PARTITION BY Team ORDER BY Price_in_cr DESC) AS RankWithinTeam
    FROM IPLPlayers
)
SELECT 
    Player, 
    Team, 
    Price_in_cr, 
    RankWithinTeam
FROM RankedPlayer
WHERE RankWithinTeam <= 2;
-- Find the most expensive player from each team, along with second most expensive player's name,price.
WITH RankedPlayers AS (
    SELECT 
        Player, 
        Team, 
        Price_in_cr,
        ROW_NUMBER() OVER (PARTITION BY Team ORDER BY Price_in_cr DESC) AS PriceRank
    FROM IPLPlayers
)
SELECT 
    Team,
    MAX(CASE WHEN PriceRank = 1 THEN Player END) AS Most_Expensive_Player,
    MAX(CASE WHEN PriceRank = 1 THEN Price_in_cr END) AS Most_Expensive_Price,
    MAX(CASE WHEN PriceRank = 2 THEN Player END) AS Second_Most_Expensive_Player,
    MAX(CASE WHEN PriceRank = 2 THEN Price_in_cr END) AS Second_Most_Expensive_Price
FROM RankedPlayers
WHERE PriceRank IN (1, 2)
GROUP BY Team
ORDER BY Team;
-- calculate the percentage contribution of each player's price to their team's total spending.
WITH TeamTotal AS (
    SELECT 
        Team,
        SUM(Price_in_cr) AS Total_Team_Spending
    FROM IPLPlayers
    GROUP BY Team
)
SELECT 
    p.Player,
    p.Team,
    p.Price_in_cr,
    t.Total_Team_Spending,
    ROUND((p.Price_in_cr / t.Total_Team_Spending) * 100, 2) AS Percentage_Contribution
FROM IPLPlayers p
JOIN TeamTotal t ON p.Team = t.Team
ORDER BY p.Team, Percentage_Contribution DESC;
-- classify players as 'High', 'Medium' or 'Low priced based on the following rules.
-- High : Price > 15 crore 
-- Medium : price between 5 crore and 15 crore 
-- Low : Price < 5 crore and find out the Players in each bracket.
SELECT 
    CASE 
        WHEN Price_in_cr > 15 THEN 'High'
        WHEN Price_in_cr BETWEEN 5 AND 15 THEN 'Medium'
        WHEN Price_in_cr < 5 THEN 'Low'
    END AS Price_Bracket,
    COUNT(*) AS Player_Count
FROM IPLPlayers
GROUP BY 
    CASE 
        WHEN Price_in_cr > 15 THEN 'High'
        WHEN Price_in_cr BETWEEN 5 AND 15 THEN 'Medium'
        WHEN Price_in_cr < 5 THEN 'Low'
    END
ORDER BY 
    CASE 
        WHEN Price_in_cr > 15 THEN 'High'
        WHEN Price_in_cr BETWEEN 5 AND 15 THEN 'Medium'
        WHEN Price_in_cr < 5 THEN 'Low'
    END; 

SELECT 
    Player,
    Team,
    Price_in_cr,
    CASE 
        WHEN Price_in_cr > 15 THEN 'High'
        WHEN Price_in_cr BETWEEN 5 AND 15 THEN 'Medium'
        WHEN Price_in_cr < 5 THEN 'Low'
    END AS Price_Bracket
FROM IPLPlayers
ORDER BY Price_Bracket, Price_in_cr DESC;
-- Find the average price of Indian Players and compare it with overseas Players.
SELECT 
    CASE 
        WHEN Type LIKE '%Indian%' THEN 'Indian'
        WHEN Type LIKE '%Overseas%' THEN 'Overseas'
        ELSE 'Other'
    END AS Player_Category,
    ROUND(AVG(Price_in_cr), 2) AS Avg_Price
FROM IPLPlayers
GROUP BY 
    CASE 
        WHEN Type LIKE '%Indian%' THEN 'Indian'
        WHEN Type LIKE '%Overseas%' THEN 'Overseas'
        ELSE 'Other'
    END;
-- Identify players who earn more then the average price in their team.
WITH TeamAvg AS (
    SELECT 
        Team,
        AVG(Price_in_cr) AS Avg_Team_Price
    FROM IPLPlayers
    GROUP BY Team
)
SELECT 
    p.Player,
    p.Team,
    p.Price_in_cr,
    t.Avg_Team_Price
FROM IPLPlayers p
JOIN TeamAvg t ON p.Team = t.Team
WHERE p.Price_in_cr > t.Avg_Team_Price
ORDER BY p.Team, p.Price_in_cr DESC;
-- For each role , Find the most expensive player and their price using a correlated subquery.
SELECT 
    Player,
    Role,
    Price_in_cr
FROM IPLPlayers p
WHERE Price_in_cr = (
    SELECT MAX(Price_in_cr)
    FROM IPLPlayers
    WHERE Role = p.Role
)
ORDER BY Role;




 
  






